    private $data,$act_permission,$module="reports.closeday",$id=1;

    public function __construct()
    {
        $this->act_permission = DB::select("
            select sum(coalesce(allow_create,0)) as allow_create,sum(coalesce(allow_delete,0)) as allow_delete,sum(coalesce(allow_show,0)) as allow_show,sum(coalesce(allow_edit,0)) as allow_edit from (
                select count(1) as allow_create,0 as allow_delete,0 as allow_show,0 as allow_edit from permissions p join role_has_permissions rp on rp.permission_id = p.id where rp.role_id = 1 and p.name like '%.create' and p.name like '".$this->module.".%'
                union 
                select 0 as allow_create,count(1) as allow_delete,0 as allow_show,0 as allow_edit from permissions p  join role_has_permissions rp on rp.permission_id = p.id where rp.role_id = 1 and p.name like '%.delete' and p.name like '".$this->module.".%'
                union 
                select 0 as allow_create,0 as allow_delete,count(1) as allow_show,0 as allow_edit from permissions p  join role_has_permissions rp on rp.permission_id = p.id where rp.role_id = 1 and p.name like '%.show' and p.name like '".$this->module.".%'
                union 
                select 0 as allow_create,0 as allow_delete,0 as allow_show,count(1) as allow_edit from permissions p  join role_has_permissions rp on rp.permission_id = p.id where rp.role_id = 1 and p.name like '%.edit' and p.name like '".$this->module.".%'
            ) a
        ");
        // Closure as callback
        
    }


    public function index(Request $request) 
    {
        $user = Auth::user();
        $id = $user->roles->first()->id;
        $this->getpermissions($id);

        $period = DB::select("select period_no,remark from period where period_no<=to_char(now(),'YYYYMM')::int and period_no>=202301  order by period_no asc");
        
        $branchs = Branch::join('users_branch as ub','ub.branch_id', '=', 'branch.id')->where('ub.user_id','=',$user->id)->get(['branch.id','branch.remark']);        

        $shifts = Shift::orderBy('shift.id')->get(['shift.id','shift.remark','shift.id','shift.time_start','shift.time_end']); 
        $report_data = DB::select("
                select b.id as branch_id,b.remark as branch_name,im.dated,sum(id.total+id.vat_total) as total_all,
                sum(case when ps.type_id = 2 and ps.category_id = 53 then id.total+id.vat_total else 0 end) as total_salon,
                sum(case when ps.type_id = 2 and ps.category_id !=53 then id.total+id.vat_total else 0 end) as total_service,
                sum(case when ps.type_id = 1 and ps.category_id !=26 then id.total+id.vat_total else 0 end) as total_product,
                sum(case when ps.type_id = 1 and ps.category_id = 26 then id.total+id.vat_total else 0 end) as total_drink,
                sum(case when ps.type_id = 8 and ps.remark not like '%CHARGE LEBARAN%'  then id.total+id.vat_total else 0 end) as total_extra,
                sum(case when ps.type_id = 8 and ps.remark like '%CHARGE LEBARAN%'  then id.total+id.vat_total else 0 end) as total_lebaran,
                sum(case when im.payment_type = 'Cash' then id.total+id.vat_total else 0 end) as total_cash,
                sum(case when im.payment_type = 'BCA - Debit' then id.total+id.vat_total else 0 end) as total_b_d,
                sum(case when im.payment_type = 'BCA - Kredit' then id.total+id.vat_total else 0 end) as total_b_k,
                sum(case when im.payment_type = 'Mandiri - Debit' then id.total+id.vat_total else 0 end) as total_m_d,
                sum(case when im.payment_type = 'Mandiri - Kredit' then id.total+id.vat_total else 0 end) as total_m_k,
                sum(case when im.payment_type = 'QRIS' then id.total+id.vat_total else 0 end) as total_qr,
                sum(case when im.payment_type = 'Transfer' then id.total+id.vat_total else 0 end) as total_tr,
                sum(case when im.payment_type = 'BCA - Debit' or im.payment_type = 'BANK 1 - Debit' then id.total+id.vat_total else 0 end) as total_b1d,
                sum(case when im.payment_type = 'BCA - Kredit' or im.payment_type = 'BANK 1 - Kredit' then id.total+id.vat_total else 0 end) as total_b1c,
                sum(case when im.payment_type = 'Mandiri - Debit' or im.payment_type = 'BANK 2 - Debit' then id.total+id.vat_total else 0 end) as total_b2d,
                sum(case when im.payment_type = 'Mandiri - Kredit' or im.payment_type = 'BANK 2 - Kredit' then id.total+id.vat_total else 0 end) as total_b2c,
                sum(case when im.payment_type = 'Transfer' or im.payment_type = 'BANK 1 - Transfer' then id.total+id.vat_total else 0 end) as total_b1t,
                sum(case when im.payment_type = 'BANK 2 - Transfer' then id.total+id.vat_total else 0 end) as total_b2t,
                sum(case when im.payment_type = 'QRIS' or im.payment_type = 'BANK 1 - QRIS' then id.total+id.vat_total else 0 end) as total_b1q,
                sum(case when im.payment_type = 'BANK 2 - QRIS' then id.total+id.vat_total else 0 end) as total_b2q,
                count(distinct im.invoice_no) qty_transaction,count(distinct im.invoice_no) qty_customers,
                sum(case when ps.type_id = 2 then id.qty else 0 end) as qty_service
                from invoice_master im 
                join invoice_detail id on id.invoice_no  = im.invoice_no 
                join product_sku ps on ps.id = id.product_id 
                join customers c on c.id = im.customers_id 
                join branch b on b.id = c.branch_id
                join users_branch as ub on ub.branch_id = b.id and ub.user_id = '".$user->id."'
                where im.dated>now()-interval'7 days'
                group by b.remark,im.dated,b.id       
        ");
        $data = $this->data;
        $keyword = "";
        $act_permission = $this->act_permission[0];
        return view('pages.reports.close_day',['company' => Company::get()->first()], compact('period','shifts','branchs','data','keyword','act_permission','report_data'))->with('i', ($request->input('page', 1) - 1) * 5);
    }

    public function getdata(Request $request) 
    {
        $user = Auth::user();
        $id = $user->roles->first()->id;
        $this->getpermissions($id);

        $data = $this->data;
        $keyword = "";
        $act_permission = $this->act_permission[0];
        $branchs = Branch::join('users_branch as ub','ub.branch_id', '=', 'branch.id')->where('ub.user_id','=',$user->id)->get(['branch.id','branch.remark']);        

        $shifts = Shift::orderBy('shift.id')->get(['shift.id','shift.remark','shift.id','shift.time_start','shift.time_end']); 
        $filter_begin_date = date(Carbon::parse($request->filter_begin_date)->format('Y-m-d'));
        $filter_branch_id =  $request->get('filter_branch_id')==null?'%':$request->get('filter_branch_id');
        $report_data = DB::select("
        select category_id,branch_name,dated,product_name,abbr,type_id,price,qty,case when total=0 then 'Free' else total::character varying end as total,qty_customer from (
                select ps.category_id,b.remark as branch_name,im.dated,id.product_name,ps.abbr,ps.type_id,id.price,sum(id.qty) as qty,sum(id.total+id.vat_total) as total,count(c.id) as qty_customer
                from invoice_master im 
                join invoice_detail id on id.invoice_no = im.invoice_no 
                join customers c on c.id = im.customers_id 
                join branch b on b.id=c.branch_id
                join product_sku ps on ps.id = id.product_id 
                where id.total>0 and im.dated = '".$filter_begin_date."'  and c.branch_id = ".$filter_branch_id." and ps.id!=461
                group by ps.category_id,b.remark,im.dated,id.product_name,ps.abbr,id.price,ps.type_id     
                union all
                select ps.category_id,b.remark as branch_name,im.dated,id.product_name,ps.abbr,ps.type_id,id.price,sum(id.qty) as qty,sum(id.total+id.vat_total) as total,count(c.id) as qty_customer
                from invoice_master im 
                join invoice_detail id on id.invoice_no = im.invoice_no 
                join customers c on c.id = im.customers_id 
                join branch b on b.id=c.branch_id
                join product_sku ps on ps.id = id.product_id 
                where id.total=0  and id.discount=id.price*id.qty  and ps.id!=461 and im.dated = '".$filter_begin_date."'  and c.branch_id = ".$filter_branch_id."
                group by ps.category_id,b.remark,im.dated,id.product_name,ps.abbr,id.price,ps.type_id     
        ) a    

        ");

        $creator = DB::select("
                select string_agg(distinct created_by,', ') created_by from (select coalesce(u.name,'-') as created_by
                from invoice_master im 
                join customers c on c.id= im.customers_id 
                join users u on u.id= im.created_by
                where im.dated = '".$filter_begin_date."'  and c.branch_id = ".$filter_branch_id." order by im.invoice_no ) a                      
        ");
        $out_data = DB::select("
                select ps2.abbr,sum(pi2.qty*id.qty) as qty 
                from invoice_master im 
                join invoice_detail id on id.invoice_no = im.invoice_no 
                join customers c on c.id = im.customers_id 
                join branch b on b.id=c.branch_id
                join product_sku ps on ps.id = id.product_id 
                join product_ingredients pi2 on pi2.product_id = ps.id 
                join product_distribution pdd on pdd.product_id = pi2.product_id_material and pdd.active=1 and pdd.branch_id = b.id
                join product_sku ps2 on ps2.id = pi2.product_id_material  
                where im.dated = '".$filter_begin_date."'  and c.branch_id = ".$filter_branch_id."
                group by ps2.abbr                    
        ");
        $payment_data = DB::select("
                select im.invoice_no,im.total_payment,im.payment_type,count(distinct im.invoice_no) as qty_payment
                from invoice_master im 
                join customers c on c.id = im.customers_id 
                join branch b on b.id=c.branch_id 
                where im.dated = '".$filter_begin_date."'  and c.branch_id = ".$filter_branch_id."  
                group by im.invoice_no,im.total_payment,im.payment_type                       
        ");


        $petty_datas = DB::select("
            select ps.abbr,pc.type,sum(pcd.qty) qty,sum(pcd.line_total) as total  from petty_cash pc 
            join petty_cash_detail pcd on pcd.doc_no = pc.doc_no
            join product_sku ps on ps.id = pcd.product_id 
            where pc.dated  = '".$filter_begin_date."'  and pc.branch_id = ".$filter_branch_id."  
            group by ps.abbr,pc.type order by 1                   
        ");

        $cust = DB::select("
                select count(c.id) as c_cus
                from invoice_master im 
                join customers c on c.id = im.customers_id 
                join branch b on b.id=c.branch_id 
                where im.dated = '".$filter_begin_date."'  and c.branch_id = ".$filter_branch_id."                      
        ");
        $payment_type = ['Cash','BCA - Debit','BCA - Kredit','Mandiri - Debit','Mandiri - Kredit','Transfer','QRIS'];
        $users = User::join('users_branch as ub','ub.branch_id', '=', 'users.branch_id')->where('ub.user_id','=',$user->id)->where('users.job_id','=',2)->get(['users.id','users.name']);

        if($request->filter_type == "api"){
            return array_merge([
                'data' => $data,
                'payment_datas' => $payment_data,
                'out_datas' => $out_data,
                'report_datas' => $report_data,
                'petty_datas' => $petty_datas,
                'cust' => $cust,
                'creator' => $creator,
                'settings' => Settings::get(),
                'filter_begin_date' => $filter_begin_date,
                'filter_begin_end' => $filter_begin_date,
                'filter_branch_id' => $filter_branch_id,
            ]);
        }else{
            return view('pages.reports.close_day_print', [
                'data' => $data,
                'payment_datas' => $payment_data,
                'out_datas' => $out_data,
                'report_datas' => $report_data,
                'petty_datas' => $petty_datas,
                'cust' => $cust,
                'creator' => $creator,
                'settings' => Settings::get(),
                'filter_begin_date' => $filter_begin_date,
                'filter_begin_end' => $filter_begin_date,
                'filter_branch_id' => $filter_branch_id,
            ]);
        }
        
    }

