            group by total_324,total_325,total_326,total_327,total_328,total_316,total_309,total_oth,total_318,total_319,total_280,total_281,total_282,total_283,total_284,total_285,total_286,total_287,total_288,total_289,total_290,total_291,total_292,total_293,total_294,total_295,total_296,total_297,total_298,total_299,total_300,total_301,total_302,total_304, total_305,total_306,total_307,total_308,total_310,total_312,total_313,total_315,total_317,total_321

            ");

        

            return array_merge(
                    [
                        'company' => Company::get()->first(),
                        'report_datas'=> $report_data,
                        'dtt_detail'=> $report_data,
                        'dtt_raw_oneline' => $dtt_raw_oneline,
                        'filter_begin_date' => $begindate,
                        'filter_begin_end' => $enddate,
                        'filter_branch_id' => $branchx,
                    ]);

        }else{
            $report_data = DB::select("
                select c.gender,b.remark as branch_name,im.dated,im.invoice_no,id.product_name,pc.remark as category_name,id.qty,id.uom,id.total+id.vat_total as total
                from invoice_master im 
                join invoice_detail id on id.invoice_no = im.invoice_no 
                join product_sku ps on ps.id = id.product_id 
                join product_category pc on pc.id = ps.category_id 
                join customers c on c.id = im.customers_id  and c.branch_id::character varying like '%".$branchx."%'
                join users u on u.id=im.created_by
                join branch b on b.id = c.branch_id
                join users_branch as ub on ub.branch_id = b.id and ub.user_id = '".$user->id."'
                where im.dated between '".$begindate."' and '".$enddate."'                 
            ");         
            return view('pages.reports.invoicedetail',['company' => Company::get()->first()], compact('shifts','branchs','data','keyword','act_permission','report_data'))->with('i', ($request->input('page', 1) - 1) * 5);
        }
    }


    public function getpermissions($role_id){
        $id = $role_id;
        $permissions = Permission::join('role_has_permissions',function ($join)  use ($id) {
            $join->on(function($query) use ($id) {
                $query->on('role_has_permissions.permission_id', '=', 'permissions.id')
                ->where('role_has_permissions.role_id','=',$id)->where('permissions.name','like','%.index%')->where('permissions.url','!=','null');
